    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String s = jTextField1.getText();
        int paklaida;
        try {
            paklaida = Integer.parseInt(s);
            if (paklaida < 0) {
                jTextArea1.setText("Negalima vesti neigiamų skaičių.");
                return;
            }
        } catch (Exception ex) {
            jTextArea1.setText("Neteisingai įvesta paklaida.");   
            return;
        }
        if (jCheckBox2.isSelected()) {
            Apfloat pi = Metodai.Cpi(paklaida);
            jTextArea1.setText(pi.toString(true));
        }
        else {
            Apfloat pi = Metodai.GLpi(paklaida);
            jTextArea1.setText(pi.toString(true));   
        }       
    }//GEN-LAST:event_jButton3ActionPerformed

