    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        Apfloat e, pi;
        String s = jTextField2.getText();
        int paklaida;
        try {
            paklaida = Integer.parseInt(s) + 2;
            if (paklaida < 2) {
                jTextArea2.setText("Negalima vesti neigiamų skaičių.");
                return;
            }
        } catch (Exception ex) {
            jTextArea2.setText("Neteisingai įvesta paklaida.");   
            return;
        }
        if (jCheckBox4.isSelected()) {
            e = Metodai.Ne(paklaida);
        }
        else {
            e = Metodai.Oe(paklaida);
        }
        if (jCheckBox2.isSelected()) {
            pi = Metodai.Cpi(paklaida);
        }
        else {
            pi = Metodai.GLpi(paklaida);
        }
        Apfloat epi = Metodai.epi(e, pi, paklaida);
        jTextArea2.setText(epi.toString(true));
    }//GEN-LAST:event_jButton2ActionPerformed

